import DayPickerInput from "react-day-picker/DayPickerInput";
import "react-day-picker/lib/style.css";
import dateFnsFormat from "date-fns/format";
import dateFnsParse from "date-fns/parse";
import { DateUtils } from "react-day-picker";
import { useState } from "react";
import calculateNights from "../utils/calculate-nights";

const parseDate = (str, format, locale) => {
  const parsed = dateFnsParse(str, format, new Date(), { locale });
  return DateUtils.isDate(parsed) ? parsed : null;
};

const formatDate = (date, format, locale) =>
  dateFnsFormat(date, format, { locale });

const format = "dd MMM yyyy";

// const numberOfNightsBetweenDates = (startDate, endDate) => {
//   const start = new Date(startDate); //clone
//   const end = new Date(endDate); //clone
//   let dayCount = 0;

//   while (end > start) {
//     dayCount++;
//     start.setDate(start.getDate() + 1);
//   }

//   return dayCount;
// };

export default ({ datesChanged }) => {
  const today = new Date();
  const tomorrow = new Date(today);
  tomorrow.setDate(tomorrow.getDate() + 1);
  const [startDate, setStartDate] = useState(today);
  const [endDate, setEndDate] = useState(tomorrow);
  return (
    <div className="date-range-picker-container">
      <div>
        <label htmlFor="">From:</label>
        <DayPickerInput
          formatDate={formatDate}
          format={format}
          value={startDate}
          parseDate={parseDate}
          placeholder={`${dateFnsFormat(startDate, format)}`}
          dayPickerProps={{
            modifiers: {
              disabled: {
                before: startDate
              }
            }
          }}
          onDayChange={day => {
            setStartDate(day);
            const newEndDate = new Date(day);
            if (calculateNights(day, endDate) < 1) {
              newEndDate.setDate(newEndDate.getDate() + 1);
              setEndDate(newEndDate);
            }
            datesChanged(day, newEndDate);
          }}
        />
      </div>
      <div>
        <label htmlFor="">To:</label>
        <DayPickerInput
          formatDate={formatDate}
          format={format}
          parseDate={parseDate}
          value={endDate}
          placeholder={`${dateFnsFormat(endDate, format)}`}
          dayPickerProps={{
            modifiers: {
              disabled: [
                startDate,
                {
                  before: startDate
                }
              ]
            }
          }}
          onDayChange={day => {
            setEndDate(day);
            datesChanged(startDate, day);
          }}
        />
      </div>
      <style jsx>{`
        .date-range-picker-container div {
          display: grid;
          border: 1px solid #ddd;
          grid-template-columns: 30% 70%;
          padding: 10px;
        }
        label {
          padding-top: 10px;
        }
      `}</style>
      <style jsx global>{`
        .DayPickerInput input {
          width: 120px;
          padding: 10px;
          font-size: 16px;
        }
      `}</style>
    </div>
  );
};
