import Header from "./Header";
import Modal from "./Modal";
// import { useState } from "react";
import RegistrationModal from "../components/RegistrationModal";
import LoginModal from "../components/LoginModal";
import { useStoreState, useStoreActions } from "easy-peasy";
import { Head } from "next/head";

const Layout = props => {
  // const [showModal, setShowModal] = useState(true);
  // const [showLoginModal, setShowLoginModal] = useState(true);
  // const [showRegistrationModal, setShowRegistrationModal] = useState(false);
  const showModal = useStoreState(state => state.modals.showModal);
  const showLoginModal = useStoreState(state => state.modals.showLoginModal);
  const showRegistrationModal = useStoreState(
    state => state.modals.showRegistrationModal
  );

  const setHideModal = useStoreActions(actions => actions.modals.setHideModal);
  const setShowRegistrationModal = useStoreActions(
    actions => actions.modals.setShowRegistrationModal
  );
  const setShowLoginModal = useStoreActions(
    actions => actions.modals.setShowLoginModal
  );
  return (
    <div>
      <Header {...props}></Header>
      <main>{props.content}</main>
      {showModal && (
        <Modal
          close={() => {
            setHideModal();
          }}
        >
          {(showLoginModal && (
            <LoginModal
              showRegistration={() => {
                setShowRegistrationModal();
              }}
            />
          )) ||
            (showRegistrationModal && (
              <RegistrationModal
                showLogin={() => {
                  setShowLoginModal();
                }}
              />
            ))}
        </Modal>
      )}
      <style jsx global>{`
        body {
          margin: 0;
          font-family: Roboto, -apple-system, BlinkMacSystemFont, Segoe UI,
            Oxygen, Ubuntu, Cantarell, Fira Sans, Droid Sans, Helvetica Neue,
            sans-serif;
          font-size: 14px;
          line-height: 1.5;
          color: #333;
        }
        button {
          background-color: rgb(255, 90, 95);
          color: white;
          font-size: 13px;
          width: 100%;
          border: none;
          height: 40px;
          border-radius: 4px;
          cursor: pointer;
        }

        input[type="text"],
        input[type="email"],
        input[type="password"] {
          display: block;
          padding: 20px;
          font-size: 20px !important;
          width: 100%;
          border: 1px solid #ccc;
          border-radius: 4px;
          box-sizing: border-box;
          margin-bottom: 10px;
        }
      `}</style>
      <style jsx>{`
        main {
          position: relative;
          max-width: 56em;
          background-color: white;
          padding: 2em;
          margin: 0 auto;
          box-sizing: border-box;
        }
      `}</style>
    </div>
  );
};

export default Layout;
